    return () => {
      if (type) {
        window.removeEventListener(type, wrapper);
      }
    };
  }, [type]);
}
import React from 'react';
import { createPortal } from 'react-dom';
import './SlidePortal.less';

export interface ISliderPortalProps extends React.HTMLAttributes<HTMLDivElement> {
